KQL_query:
DeviceProcessEvents
| where FileName in ("powershell.exe","pwsh.exe")
// Look for Invoke-WebRequest or its alias downloading a .ps1
| where tolower(ProcessCommandLine) has "iwr" and tolower(ProcessCommandLine) has ".ps1"
// Look for the credential-vault dumping function
| where tolower(ProcessCommandLine) has "get-passwordvaultcredentials"
// Extract the downloaded script name to verify itâ€™s the expected tool
| extend ScriptName = extract(@"(?i)\b(GetCredmanCreds\.ps1)\b", 1, ProcessCommandLine)
| where isnotempty(ScriptName)
| project TimeGenerated, DeviceName, InitiatingProcessAccountName, ScriptName, ProcessCommandLine
