let CoreUtilities = dynamic(["cmd.exe", "powershell.exe", "rundll32.exe", "wscript.exe", "cscript.exe", "regsvr32.exe", "mshta.exe", "certutil.exe", "bitsadmin.exe", "at.exe", "schtasks.exe", "whoami.exe", "net.exe", "net1.exe"]);
// Detect the copy from System32 to user profile
let FileCopies = DeviceFileEvents
| where ActionType =~ "FileCreated" and FileName in (CoreUtilities)
| where PreviousFolderPath has "\Windows\System32" and FolderPath has "Users";
// Find executions of the newly copied utility
DeviceProcessEvents
| where FileName in (CoreUtilities)
| where FolderPath has "Users"
| join kind=inner (FileCopies) on FileName, DeviceId
| project TimeCopied = TimeGenerated1, TimeExecuted = TimeGenerated, DeviceName, DeviceId, FileName, CopiedFrom = PreviousFolderPath, CopiedTo = FolderPath1, ExecutedFrom = FolderPath
| summarize min(TimeCopied), min(TimeExecuted) by DeviceName, DeviceId, FileName, CopiedFrom, CopiedTo, ExecutedFrom

