all_attempts.query_llm:
let victimFolder = "/tmp/victim-files";
// 1. Search file-level deletion events in DeviceFileEvents and DeviceEvents
let file_deletes = union isfuzzy=true (
    DeviceFileEvents
    | where isnotempty(FolderPath) and FolderPath startswith victimFolder
    | where tolower(ActionType) has_any("delete", "remove", "unlink")
    | project TimeGenerated, DeviceName, FileName, FolderPath, ActionType, InitiatingProcessAccountName, InitiatingProcessCommandLine, Type,
    DeviceEvents
    | where isnotempty(FolderPath) and FolderPath startswith victimFolder
    | where tolower(ActionType) has_any("delete", "remove", "unlink")
    | project TimeGenerated, DeviceName, FileName, FolderPath, ActionType, InitiatingProcessAccountName, InitiatingProcessCommandLine, Type
);
// 2. Search for process executions in DeviceProcessEvents that may indicate silent deletions
let suspicious_processes = DeviceProcessEvents
| where isnotempty(ProcessCommandLine)
| where ProcessCommandLine has_any ("rm /tmp/victim-files", "rm -f /tmp/victim-files", "unlink /tmp/victim-files", "rm -rf /tmp/victim-files", "find /tmp/victim-files" )
| project TimeGenerated, DeviceName, FileName, FolderPath, ActionType="ProcessDeleteAttempt", InitiatingProcessAccountName=AccountName, InitiatingProcessCommandLine=ProcessCommandLine, Type;
file_deletes
| union suspicious_processes
| order by TimeGenerated desc

