let timeframe = 1d;

let MkDirs = DeviceProcessEvents
| where TimeGenerated >= ago(timeframe)
// Identify mkdir calls under /var/spool/mail
| where (FileName in ("bash","sh") and ProcessCommandLine has "mkdir") or FileName == "mkdir"
| where ProcessCommandLine has "/var/spool/mail/"
| extend MkDirTime = TimeGenerated, MkDirCommand = ProcessCommandLine;

let CopyOps = DeviceProcessEvents
| where TimeGenerated >= ago(timeframe)
// Identify cp calls under /var/spool/mail
| where FileName == "cp"
| where ProcessCommandLine has "/var/spool/mail/"
| extend CopyTime = TimeGenerated, CopyCommand = ProcessCommandLine;

MkDirs
| join kind=inner CopyOps on DeviceId, InitiatingProcessAccountName
// Ensure copy happens within 30 minutes of the mkdir
| where CopyTime between (MkDirTime .. MkDirTime + 30m)
| project MkDirTime, CopyTime, DeviceName, User = InitiatingProcessAccountName, MkDirCommand, CopyCommand

