all_attempts.query_llm:
let regKey = @"HKCU\Software\Classes\ms-settings\shell\open\command";
// Step 1: Get registry key modifications related to UAC bypass
let RegistryWrites = DeviceRegistryEvents
    | where ActionType in ("SetValue", "CreateKey", "ModifyKey")
    | where RegistryKey =~ regKey
    | project DeviceId, DeviceName, UserSid=InitiatingProcessAccountSid, RegistryWriteTime=TimeGenerated, WrittenExecutable=RegistryValueData;
// Step 2: Find fodhelper.exe launches by same user on same device after the registry write
let FodhelperExecs = DeviceProcessEvents
    | where FileName =~ "fodhelper.exe"
    | project DeviceId, DeviceName, UserSid=AccountSid, FodhelperTime=TimeGenerated, FodhelperCommandLine=ProcessCommandLine;
// Step 3: Join on DeviceId and UserSid, correlate times
RegistryWrites
| join kind=inner (FodhelperExecs) on DeviceId, DeviceName, UserSid
| where FodhelperTime > RegistryWriteTime and FodhelperTime < RegistryWriteTime + 15m
| project DeviceName, DeviceId, UserSid, WrittenExecutable, RegistryWriteTime, FodhelperTime, FodhelperCommandLine
| sort by RegistryWriteTime desc

