test_case.KQL_query:
// Detect enumeration of available shells on Linux (/etc/shells)
let Path = "/etc/shells";

// 1. Process executions that read /etc/shells (e.g., “cat /etc/shells”)
let proc = DeviceProcessEvents
  | where ProcessCommandLine has Path
  | project TimeGenerated, DeviceName, InitiatingProcessAccountName, ProcessCommandLine, Source = "DeviceProcessEvents";

// 2. File‐read operations against /etc/shells
let fileRead = DeviceFileEvents
  | where FolderPath == "/etc" and FileName == "shells" and ActionType has "FileRead"
  | project TimeGenerated, DeviceName, InitiatingProcessAccountName, ProcessCommandLine = InitiatingProcessCommandLine, Source = "DeviceFileEvents";

// 3. Syslog entries that include a read of /etc/shells
let sys = Syslog
  | where SyslogMessage has Path
  | project TimeGenerated, DeviceName = HostName, InitiatingProcessAccountName = "", ProcessCommandLine = SyslogMessage, Source = "Syslog";

// Combine and sort by time
proc
| union fileRead
| union sys
| sort by TimeGenerated desc

