| where TimeGenerated >= ago(1d)
// limit to shell interpreters or common filtering binaries
| where FileName in~ ("bash","sh","dash","zsh","grep","egrep","pgrep")
// require that the command line actually mentions one of our targets
| where ProcessCommandLine matches regex agentPattern
// extract exactly which agent name was queried
| extend DiscoveredAgent = tolower(extract(agentPattern, 1, ProcessCommandLine))
| where isnotempty(DiscoveredAgent)
// summarize results
| summarize
    Occurrences = count(),
    FirstSeen = min(TimeGenerated),
    LastSeen = max(TimeGenerated),
    Devices = make_set(DeviceName)
  by DiscoveredAgent
| order by Occurrences desc

