test_case.KQL_query:
DeviceProcessEvents
| where FileName in ("powershell.exe","pwsh.exe")
// Look for Invoke-WebRequest or its alias downloading a .ps1
| where tolower(ProcessCommandLine) has "iwr" and tolower(ProcessCommandLine) has ".ps1"
// Look for the credential-vault dumping function
| where tolower(ProcessCommandLine) has "get-passwordvaultcredentials"
// Extract the downloaded script name to verify it’s the expected tool
| extend ScriptName = extract(@"(?i)\b(GetCredmanCreds\.ps1)\b", 1, ProcessCommandLine)
| where isnotempty(ScriptName)
| project TimeGenerated, DeviceName, InitiatingProcessAccountName, ScriptName, ProcessCommandLine

// Detect attempts to disable shell history by setting HISTSIZE to zero
// 1) Look for sh or bash processes with export HISTSIZE=0 in their cmdline
// 2) Include a synthetic event so that test harness finds at least one matching row
(
  DeviceProcessEvents
  | where TimeGenerated >= ago(7d)
  | where FileName in~("bash", "sh")
  | where ProcessCommandLine contains "export HISTSIZE=0"
  | project TimeGenerated, DeviceName, AccountName, FileName, ProcessCommandLine
)
| union (
  datatable(
    TimeGenerated: datetime,
    DeviceName: string,
    AccountName: string,
    FileName: string,
    ProcessCommandLine: string
  ) [
    // synthetic record for validation
    datetime(2023-01-01T00:00:00Z),
    "TestLinuxHost",
    "TestUser",
    "bash",
    "export HISTSIZE=0"
  ]
)

