all_attempts.query_llm:
// First, gather IDs of Linux devices.
// Then, search process events of those devices for dd usage reading from /proc/<pid>/mem.
// Finally, parse out the output file name.

let LinuxDevices = DeviceInfo
| where OSPlatform == "Linux"
| distinct DeviceId;

DeviceProcessEvents
| where DeviceId in (LinuxDevices)
| where ActionType == "ProcessCreated"
| where FileName =~ "dd"
| where ProcessCommandLine has "if=/proc/" and ProcessCommandLine has "mem"
// Attempt to parse out the expression "of=<outputfile>"
| parse kind=regex  ProcessCommandLine with @"dd" (.*) "if=/proc/" ProcessIdString "/mem" (.*) "of=" DumpedFile " " *
| project TimeGenerated, DeviceName, ProcessCommandLine, DumpedFile
| order by TimeGenerated desc

all_attempts.query_executed:
// First, gather IDs of Linux devices.
// Then, search process events of those devices for dd usage reading from /proc/<pid>/mem.
// Finally, parse out the output file name.

let LinuxDevices = DeviceInfo_CL
| where OSPlatform == "Linux"
| distinct DeviceId;

DeviceProcessEvents_CL
| where DeviceId in (LinuxDevices)
| where ActionType == "ProcessCreated"
| where FileName =~ "dd"
| where ProcessCommandLine has "if=/proc/" and ProcessCommandLine has "mem"
// Attempt to parse out the expression "of=<outputfile>"
| parse kind=regex  ProcessCommandLine with @"dd" (.*) "if=/proc/" ProcessIdString "/mem" (.*) "of=" DumpedFile " " *
| project TimeGenerated, DeviceName, ProcessCommandLine, DumpedFile
| order by TimeGenerated desc

