test_case.KQL_query:
let CronPath = "/var/spool/cron/crontabs";

// Detect file creation or modification in the cron directory
let FileEvents = DeviceFileEvents
  | where FolderPath startswith CronPath
    and ActionType in ("FileCreated","FileModified")
  | project TimeGenerated, DeviceName, Account = InitiatingProcessAccountName, CommandLine = InitiatingProcessCommandLine, ActionType, FilePath = strcat(FolderPath, "/", FileName);

// Detect processes appending to cron files via bash, sh or tee
let ProcessEvents = DeviceProcessEvents
  | where ProcessCommandLine contains CronPath
    and FileName in ("bash","sh","tee")
  | project TimeGenerated = ProcessCreationTime, DeviceName, Account = InitiatingProcessAccountName, CommandLine = ProcessCommandLine, ActionType = "ProcessCronAppend";

// Combine and sort by time
FileEvents
| union ProcessEvents
| sort by TimeGenerated desc

