all_attempts.query_llm:
// This query searches for suspicious PowerShell commands that adjust the system clock
// by either using Set-Date -Adjust or using time-additive methods (AddDays, AddHours, etc.).
// It then parses out the numeric portion used to slip the date.

union
(
    DeviceProcessEvents
    | where ActionType =~ "ProcessCreated"
    | where FileName =~ "powershell.exe"
    // Search for commands explicitly adjusting time using Set-Date -Adjust
    | where ProcessCommandLine has_any ("Set-Date", "-Adjust")
    | parse kind=regex flags=U ProcessCommandLine with @".*-[Aa]djust\s+(?<SlippedValue>[^\s]+).*"
    | project TimeGenerated, DeviceId, DeviceName, SlippedValue, ProcessCommandLine
),
(
    DeviceProcessEvents
    | where ActionType =~ "ProcessCreated"
    | where FileName =~ "powershell.exe"
    // Search for calls to time-additive methods like AddDays, AddHours, etc.
    | where ProcessCommandLine matches regex "Add(?:Seconds|Minutes|Hours|Days)\\([^\\)]*\\)"
    | parse kind=regex flags=U ProcessCommandLine with @".*(Add(?:Seconds|Minutes|Hours|Days)\\((?<SlippedValue>[^\\)]+)\\)).*"
    | project TimeGenerated, DeviceId, DeviceName, SlippedValue, ProcessCommandLine
)
| order by TimeGenerated desc

