//  3) Searches for command lines that appear to list processes (ps) and filter them (grep) for security/monitoring.
//  4) Displays which agent name was actually observed.

let LinuxDevices = DeviceInfo
| where OSPlatform == "Linux"
| distinct DeviceId;
LinuxDevices
| join (DeviceProcessEvents
