        // Adjust search terms (e.g., "agent", "monitoring", "security") if needed.
        | where ProcessCommandLine has_any ("ps", "grep", "agent", "monitoring", "security")
       ) on DeviceId
| project TimeGenerated, DeviceName, ProcessCommandLine
| order by TimeGenerated desc

