all_attempts.query_llm:
// Identify Linux devices
let LinuxDevices = DeviceInfo
| where OSPlatform == "Linux"
| distinct DeviceId;

LinuxDevices
| join kind=inner (
    DeviceProcessEvents
    // Look for process-related events (some environments may store as "ProcessCreated")
    | where tolower(ActionType) contains "process"
    | where isnotempty(ProcessCommandLine) or isnotempty(InitiatingProcessCommandLine)
    // Ensure the command line includes both 'ps' and 'grep'
    | where (
        (tolower(ProcessCommandLine) has_all ("ps","grep"))
        or (tolower(InitiatingProcessCommandLine) has_all ("ps","grep"))
    )
    // Check for references to common security or monitoring agents
    | where (
        (tolower(ProcessCommandLine) has_any ("agent","monitoring","security","defender","falcon","sentinel","crowdstrike","nessus","clamav"))
        or (tolower(InitiatingProcessCommandLine) has_any ("agent","monitoring","security","defender","falcon","sentinel","crowdstrike","nessus","clamav"))
    )
    // Create a unified field for whichever is populated
    | extend CommandLine = coalesce(ProcessCommandLine, InitiatingProcessCommandLine)
    // Extract the observed agent name from the command line
    | extend ObservedAgent = extract(@"(agent|monitoring|security|defender|falcon|sentinel|crowdstrike|nessus|clamav)", 0, tolower(CommandLine))
) on DeviceId
| project TimeGenerated, DeviceName, CommandLine, ObservedAgent
| order by TimeGenerated desc
| limit 100

