let suspicious_time_range = 7d;
// Fetch recent file events
let recent_file_modifications = DeviceFileEvents
| where TimeGenerated >= ago(30d)
| project FileName, FolderPath, InitiatingProcessCreationTime, TimeGenerated;
// Fetch registry changes indicating possible timestamp tweaks
let suspicious_registry_changes = DeviceRegistryEvents
| where TimeGenerated >= ago(30d)
| where RegistryKey matches regex @".*modified.*" or RegistryValueData matches regex @".*(date|time|timestamp).*"
| project RegistryKey, RegistryValueData, TimeGenerated;
// Join file events with registry changes on matching filenames and paths
recent_file_modifications
| join kind=inner (
suspicious_registry_changes
| project RegistryKey, RegistryValueData, TimeGenerated
) on $left.FileName == $right.RegistryKey
| where InitiatingProcessCreationTime != TimeGenerated
| order by TimeGenerated desc

