    DeviceProcessEvents
    | where TimeGenerated between (startTime .. endTime)
    | where ProcessCommandLine contains "Get-PnpDevice" or ProcessCommandLine contains "Get-WmiObject" or ProcessCommandLine contains "Get-CimInstance"
), (
    DeviceLogonEvents
    | where TimeGenerated between (startTime .. endTime)
    | where ProcessCommandLine contains "Get-PnpDevice" or ProcessCommandLine contains "Get-WmiObject" or ProcessCommandLine contains "Get-CimInstance"
)
| project TimeGenerated, DeviceName, InitiatingProcessFileName, ProcessCommandLine
| order by TimeGenerated desc

