let timeframe = 7d; // Adjust the timeframe as needed
// Find registry modifications to the specific key
registry_mods = DeviceRegistryEvents 
| where RegistryKey == @"HKCU\Software\Classes\ms-settings\shell\open\command" 
| where TimeGenerated >= ago(timeframe) 
| project TimeGenerated, PreviousRegistryValueData, DeviceId;

// Find process creation events for fodhelper.exe after the registry modification
fodhelper_launch = DeviceProcessEvents 
| where FileName == "fodhelper.exe" 
| where TimeGenerated >= ago(timeframe) 
| project TimeGenerated, InitiatingProcessId, DeviceId;

// Join registry modifications with fodhelper.exe launches on the same device occurring after the modification
result = registry_mods
| join kind=inner (
    fodhelper_launch
) on DeviceId
| where fodhelper_launch.TimeGenerated > registry_mods.TimeGenerated
| project registry_mods.TimeGenerated, registry_mods.PreviousRegistryValueData, fodhelper_launch.TimeGenerated, fodhelper_launch.InitiatingProcessId, fodhelper_launch.DeviceId
| order by fodhelper_launch.TimeGenerated desc;

result

