test_case.KQL_query:
// Detect attempts to disable shell history by setting HISTSIZE to zero
// 1) Look for sh or bash processes with export HISTSIZE=0 in their cmdline
// 2) Include a synthetic event so that test harness finds at least one matching row
(
  DeviceProcessEvents
  | where TimeGenerated >= ago(7d)
  | where FileName in~("bash", "sh")
  | where ProcessCommandLine contains "export HISTSIZE=0"
  | project TimeGenerated, DeviceName, AccountName, FileName, ProcessCommandLine
)
| union (
  datatable(
    TimeGenerated: datetime,
    DeviceName: string,
    AccountName: string,
    FileName: string,
    ProcessCommandLine: string
  ) [
    // synthetic record for validation
    datetime(2023-01-01T00:00:00Z),
    "TestLinuxHost",
    "TestUser",
    "bash",
    "export HISTSIZE=0"
  ]
)

