all_attempts.query_llm:
// Identify Linux devices
let LinuxDevices = DeviceInfo
| where TimeGenerated >= ago(30d)
| where OSPlatform == "Linux"
| distinct DeviceId;

LinuxDevices
| join kind=inner (
    DeviceProcessEvents
    | where TimeGenerated >= ago(30d)
    | where ActionType == "ProcessCreated"
    // Check for elevated tokens
    | where ProcessTokenElevation == "Elevated" or InitiatingProcessTokenElevation == "Elevated"
    // Look for commands referencing /etc/shadow and the redirection symbol
    | where ProcessCommandLine has "/etc/shadow" and ProcessCommandLine has ">"
    // Extract the file name after the '>' symbol
    | extend RedirectedFile = extract(@">\s*(\S+)", 1, ProcessCommandLine)
    | where isnotempty(RedirectedFile)
    | project TimeGenerated, DeviceId, DeviceName, ProcessCommandLine, RedirectedFile
)
on DeviceId
// Show the resulting file name and other details
| project TimeGenerated, DeviceName, ProcessCommandLine, RedirectedFile
| order by TimeGenerated desc

