query_result.query:
// Identify Linux devices
let linuxDevices = DeviceInfo
    | where OSPlatform contains "Linux"
    | project DeviceId, DeviceName;

// Network-based ping events (ICMP by ping)
let pingNetwork = DeviceNetworkEvents
    | where DeviceId in (linuxDevices | project DeviceId)
    | where Protocol == "ICMP"
    | where InitiatingProcessFileName in ("ping", "ping6")
    | extend ProcessCommandLine = InitiatingProcessCommandLine
    | project TimeGenerated, DeviceId, PingTarget = RemoteIP, ProcessCommandLine;

// Process-based ping events (extract IP from command line)
let pingProcess = DeviceProcessEvents
    | where DeviceId in (linuxDevices | project DeviceId)
    | where FileName in ("ping", "ping6")
    | extend PingTarget = extract(@"(\d+\.\d+\.\d+\.\d+)", 1, ProcessCommandLine)
    | project TimeGenerated, DeviceId, PingTarget, ProcessCommandLine;

// Combine, enrich, filter, and sort
union pingNetwork, pingProcess
| where isnotempty(PingTarget)
| join kind=inner linuxDevices on DeviceId
| sort by TimeGenerated desc
| project TimeGenerated, DeviceName, PingTarget, ProcessCommandLine

all_attempts.query_llm:
// 1) Identify Linux hosts
let linuxDevices =
    DeviceInfo
    | where OSPlatform contains "Linux"
    | project DeviceId;

// 2) ICMP network events initiated by ping
let pingNetwork =
    DeviceNetworkEvents
    | where DeviceId in (linuxDevices)
    | where Protocol == "ICMP" and InitiatingProcessFileName == "ping"
    | project TimeGenerated, DeviceName, PingTarget = RemoteIP, ProcessCommandLine;

// 3) Process events for ping commands
let pingProcess =
    DeviceProcessEvents
    | where DeviceId in (linuxDevices)
    | where FileName in ("ping", "ping6")
    // extract the first IPv4 address in the command line
    | extend PingTarget = extract("(\\d+\\.\\d+\\.\\d+\\.\\d+)", 1, ProcessCommandLine)
    | project TimeGenerated, DeviceName, PingTarget, ProcessCommandLine;

// 4) Combine and sort
union pingNetwork, pingProcess
| sort by TimeGenerated desc
| project TimeGenerated, DeviceName, PingTarget, ProcessCommandLine

