all_attempts.query_llm:
DeviceFileEvents
| where ActionType == "FileModified"
// Extract previous file size from AdditionalFields
| extend prevSize = tolong(AdditionalFields["PreviousFileSize"])
// Filter for size increase of exactly one byte
| where FileSize - prevSize == 1
// Show when and where padding occurred
