all_attempts.query_llm:
// 1. Identify Linux devices
let linuxDevices = DeviceInfo
| where TimeGenerated between (startTime .. endTime)
| where OSPlatform == "Linux"
| distinct DeviceId;

// 2. Find base64 decode commands that write out a script
let decoded = DeviceProcessEvents
| where TimeGenerated between (startTime .. endTime)
| where DeviceId in (linuxDevices)
// look for base64 decoding to file
| where ProcessCommandLine has "base64" and (ProcessCommandLine has "-d" or ProcessCommandLine has "--decode")
// extract the output path after the ">" redirection
| extend OutPath = extract(@">\s*(\S+)", 1, ProcessCommandLine)
| where isnotempty(OutPath)
// get just the filename portion
| extend ScriptName = extract(@"[^/]+$", 0, OutPath)
| project DeviceId, ScriptName, decodeTime = TimeGenerated;

// 3. Find execution of the decoded script
let executed = DeviceProcessEvents
| where TimeGenerated between (startTime .. endTime)
| where DeviceId in (linuxDevices)
| where ActionType == "ProcessCreated"
| project DeviceId, ScriptName = FileName, executionTime = TimeGenerated;

// 4. Join decode and execution events, ensure execution after decode, and list distinct script names
decoded
| join kind=inner executed on DeviceId, ScriptName
| where executionTime > decodeTime
| distinct ScriptName

