all_attempts.query_llm:
// 1) Gather process‐based service creation attempts
deviceserviceEvents = DeviceProcessEvents
| where TimeGenerated between (startofday(ago(7d)) .. now())
| where (
    // sc.exe service create command
    FileName =~ "sc.exe" and ProcessCommandLine has_cs "create"
    // or PowerShell New-Service
    or tolower(ProcessCommandLine) has "new-service"
  )
| extend DetectedName = extract(@"(?:create|New-Service)\s+""?(\S+)""?", 1, ProcessCommandLine)
| where DetectedName has_cs "w32time"
| project TimeGenerated, DeviceName, InitiatingUser=InitiatingProcessAccountName, Tool=FileName, CommandLine=ProcessCommandLine, ServiceName=DetectedName;

// 2) Catch direct registry key writes for service definitions matching w32time*
registryServiceEvents = DeviceRegistryEvents
| where TimeGenerated between (startofday(ago(7d)) .. now())
| where RegistryKey has_cs @"HKLM\\SYSTEM\\CurrentControlSet\\Services\\w32time"
| where ActionType in ("RegistryValueSet", "RegistryKeyCreate")
| project TimeGenerated, DeviceName, InitiatingUser=InitiatingProcessAccountName, RegistryKey, RegistryValueName, RegistryValueData;

// Combine and sort
union isfuzzy=true deviceserviceEvents, registryServiceEvents
| sort by TimeGenerated desc

