all_attempts.query_llm:
DeviceProcessEvents
| where TimeGenerated between (startTime .. endTime)
// Only consider PowerShell host
| where FileName =~ "powershell.exe"
// Look for common download/execute patterns
| where ProcessCommandLine has_any ("New-Object System.Net.WebClient","Invoke-WebRequest","DownloadString","Start-BitsTransfer","curl","wget")
// Try to extract an explicit -OutFile argument if present
| extend OutFileName = extract(@"-OutFile\s+['\"]?([^'\"\s]+)", 1, ProcessCommandLine)
// Try to parse the .ps1 filename from any URL or DownloadString call
| extend UrlFileName = extract(@"(?:https?://|\\/\\/)[^\s'\"]+\/([^\/]*\.ps1)", 1, ProcessCommandLine)
// Coalesce to pick whichever yielded a filename
| extend ScriptName = coalesce(OutFileName, UrlFileName)
| where isnotempty(ScriptName)
// Summarize results
| summarize Occurrences = count(), Devices = make_set(DeviceName) by ScriptName
| project ScriptName, Occurrences, Devices
| order by Occurrences desc

