all_attempts.query_llm:
DeviceProcessEvents
| where TimeGenerated between (startTime .. endTime)
// Filter for elevated SecEdit.exe executions
| where FileName =~ "secedit.exe"
| where ProcessTokenElevation == "Elevated"
// Only include commands that export the policy
| where ProcessCommandLine has "/export"
// Extract the file name specified after /cfg (handles = or space delimiter, optional quotes)
| extend ExportedFileRaw = extract(@"(?i)/cfg(?:=|\s+)([^""\s]+)", 1, ProcessCommandLine)
// Remove any surrounding quotes from the extracted file name
| extend ExportedFile = trim('"', ExportedFileRaw)
// Select relevant fields and order by time
| project TimeGenerated, DeviceName, InitiatingProcessAccountName, ExportedFile, ProcessCommandLine
| sort by TimeGenerated desc

