    "HKEY_LOCAL_MACHINE\\SOFTWARE\\Microsoft\\Active Setup\\Installed Components\\",
    "HKEY_LOCAL_MACHINE\\SOFTWARE\\Wow6432Node\\Microsoft\\Active Setup\\Installed Components\\"
]);
DeviceRegistryEvents
| where tolower(ActionType) == "registrykeycreated"
// Filter on the two standard hives for Installed Components
| where array_index_of(basePaths, p -> tolower(RegistryKey) startswith tolower(p)) >= 0
// Ensure the launcher is runonce.exe
