// Extract the component name (subkey under Installed Components)
| extend ComponentName = extract(@"Installed Components\\([^\\]+)", 1, RegistryKey)
| where isnotempty(ComponentName)
// Summarize by component and device
| summarize FirstSeen = min(TimeGenerated), LastSeen = max(TimeGenerated)
    by ComponentName, DeviceName, RegistryKey, InitiatingProcessCommandLine
| project FirstSeen, LastSeen, DeviceName, ComponentName, RegistryKey, InitiatingProcessCommandLine

