| where Line matches regex @"^\s*[A-Za-z_][A-Za-z0-9_-]*\s*\("
// Extract the function name from each call
| extend FunctionName = extract(@"^\s*([A-Za-z_][A-Za-z0-9_-]*)\s*\(", 1, Line)
// Count how often each function is invoked
| summarize InvocationCount = count() by FunctionName
| sort by InvocationCount desc

