all_attempts.query_llm:
let decodeEvents =
    DeviceProcessEvents
    | where InitiatingProcessCommandLine has "base64" and InitiatingProcessCommandLine has "-d" and InitiatingProcessCommandLine has ">"
    | extend DecodedScript = extract(@">\s*([./\\w-]+\.sh)", 1, InitiatingProcessCommandLine)
    | where isnotempty(DecodedScript)
    | project DeviceId, DecodedScript, DecodedTime = ProcessCreationTime;

let execEvents =
    DeviceProcessEvents
    | where FileName endswith ".sh"
    | project DeviceId, ExecutedScript = FileName, ExecutedTime = ProcessCreationTime;

// Normalize filenames by extracting the base filename
let normalizedDecodeEvents =
    decodeEvents
    | extend NormDecoded = tostring(split(DecodedScript, "/")[-1]);

let normalizedExecEvents =
    execEvents
    | extend NormExecuted = tostring(split(ExecutedScript, "/")[-1]);

normalizedDecodeEvents
| join kind=inner normalizedExecEvents on DeviceId
| where ExecutedTime > DecodedTime
| where NormExecuted == NormDecoded
| summarize Script = any(ExecutedScript) by DeviceId
| project Script

