all_attempts.query_llm:
DeviceRegistryEvents
| where ActionType == "RegistryValueSet"
// Look for registry changes that indicate a .NET tracing variable was modified
| where RegistryValueName contains "Tracing" or RegistryValueName contains ".NET"
// Only select events where the value indicates that tracing was turned off
| where tolower(RegistryValueData) in ("0", "off")
// Summarize by the initiating process to see which built-in tool (e.g. reg.exe, regedit.exe) was used
| summarize EventCount = count() by InitiatingProcessFileName, InitiatingProcessCommandLine
| order by EventCount desc
| where isnotempty(InitiatingProcessFileName)

