all_attempts.query_llm:
DeviceRegistryEvents
| where ActionType == "RegistryValueSet"
// Look for registry changes involving .NET tracing environment variables
| where RegistryValueName has_any ("Tracing", ".NET")
// Normalize the registry value data for comparison
| extend NormValue = trim(' ', tolower(RegistryValueData))
// Filter events where the tracing was turned off
| where NormValue in ("0", "off", "disabled")
// Summarize events by the process that initiated the change
| summarize EventCount = count() by InitiatingProcessFileName, InitiatingProcessCommandLine
| order by EventCount desc
// Optionally, filter out empty process names
| where isnotempty(InitiatingProcessFileName)

